	const ratio = activeUsers / maxActiveUsers;
	const seatsLeft = maxActiveUsers - activeUsers;

	if (ratio < 0.8 || ratio >= 1) {
		disableWarningBannerDiscardingDismissal();
	} else {
		enableWarningBanner(seatsLeft);
